import 'package:flutter/material.dart';
import 'package:flutter_riverpod/flutter_riverpod.dart';

// تعریف StateProvider برای مدیریت لیست تسک‌ها
final tasksProvider = StateProvider<List<String>>((ref) => []);

void main() {
  runApp(
    ProviderScope(
      child: MyApp(),
    ),
  );
}

class MyApp extends StatelessWidget {
  @override
  Widget build(BuildContext context) {
    return MaterialApp(
      home: Scaffold(
        appBar: AppBar(
          title: Text('StateProvider در Riverpod - لیست تسک‌ها'),
        ),
        body: Center(
          child: Consumer(builder: (context, watch, child) {
            // خواندن وضعیت لیست تسک‌ها از StateProvider
            final tasks = watch(tasksProvider);

            return Column(
              mainAxisAlignment: MainAxisAlignment.center,
              children: [
                Text('لیست تسک‌ها:', style: TextStyle(fontSize: 24)),
                for (var task in tasks.state)
                  Text(task),
                ElevatedButton(
                  onPressed: () {
                    // افزودن تسک جدید به لیست
                    tasks.state = [...tasks.state, 'تسک جدید'];
                  },
                  child: Text('افزودن تسک جدید'),
                ),
              ],
            );
          }),
        ),
      ),
    );
  }
}
